import { uiActions } from "./ui-slice";

const fetchCartData = () => {
    return dispatch => {
        const fetchData = async () => {
            const response = await fetch(
                'https://react-projects-21-adv-redux-default-rtdb.asia-southeast1.firebasedatabase.app/cart.json',
                { method: 'GET', }
            );

            if (!response.ok) {
                throw new Error('Could not fetch cart data!');
            }

            const data = await response.json();
            return data;
        };

        try {
            fetchData();
        } catch (error) {
            dispatch(uiActions.showNotification({
                status: 'error',
                title: 'Error!',
                message: 'Sending cart data failed.',
            }));
        }
    };
};

export const sendCartData = (cart) => {
    return async (dispatch) => {
        dispatch(uiActions.showNotification({
            status: 'pending',
            title: 'Sending...',
            message: 'Sending cart data!',
        }));

        const sendRequest = async () => {
            const response = await fetch('https://react-projects-21-adv-redux-default-rtdb.asia-southeast1.firebasedatabase.app/cart.json', { 
                method: 'PUT', 
                body: JSON.stringify(cart) 
            });

            if (!response.ok) {
                throw new Error('Sending cart data failed.');
            }
        };

        try {
            await sendRequest();

            dispatch(uiActions.showNotification({
                status: 'success',
                title: 'Success!',
                message: 'Sent cart data',
            }));
        } catch (error) {
            dispatch(uiActions.showNotification({
                status: 'error',
                title: 'Error!',
                message: 'Sending cart data failed.',
            }));
        }
    };
};
