import { redirect, useRouteLoaderData, Await } from 'react-router-dom';
import EventItem from '../components/EventItem';
import EventsList from '../components/EventsList';
import { Suspense } from 'react';

function EventDetailPage() {
    const {event, events} = useRouteLoaderData('event-detail'); 

    return <>
        <Suspense fallback={<p style={{textAlign: 'center'}}>Loading...</p>}>
            <Await resolve={event}>
                {loadedEvent => <EventItem event={loadedEvent} />}
            </Await>
        </Suspense>
        
        <Suspense fallback={<p style={{textAlign: 'center'}}>Loading...</p>}>
            <Await resolve={events}>
                {loadedEvents => <EventsList events={loadedEvents}/>}
            </Await>
        </Suspense>
        
    </>;
}

export default EventDetailPage;

async function loadEvent(id) {
    const response = await fetch('http://localhost:8080/events/' + id);

    if (!response.ok) {
        throw new Response(JSON.stringify({message: 'Could not fetch details for selected event'}), {
            status: 500,
        });
    } else {
        const resData = await response.json();
        return resData.event;
    }
}

async function loadEvents() {
  const response = await fetch('http://localhost:8080/events');

  if (!response.ok) {
      throw new Response(JSON.stringify({message: 'Could not fetch events'}), {
          status: 500,
      });

      //return {isError: true, message: 'Could not fetch events.'}
      
  } else {
    const resData = await response.json();
    return resData.events;
  }
}

export async function loader({request, params}) {
    const id = params.eventId;
    
    return {
        event: await loadEvent(id),
        events: loadEvents(),
    };
}

export async function action({request, params}) {
    const eventId = params.eventId;
    const response = fetch('http://localhost:8080/events/' + eventId, {
        method: request.method,
    });

    if (!response.ok) {
        throw new Response(JSON.stringify({message: 'Could not delete event.'}), {
            status: 500,
        });
    }

    return redirect('/events');
}
