import { useRouteError } from "react-router-dom";

function ErrorPage() {
    const error = useRouteError();

    let title = 'An erorr ocurred!';
    let message = 'Somethin went wrong!';

    if (error.status === 500) {
        message = error.data.message;
    }

    return <>
        <h1>An Error Occured!</h1>
    </>
}

export default ErrorPage;
